import { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import Fetch from '../components/Fetch';
import Pagination from '../components/Pagination';

import { randomInt } from '../utils/functions';

const HumorPump = () => {

    const [searchParams, setSearchParams] = useSearchParams();

    interface Puddle {
        id: string;
        body: string;
    }

    interface Data {
        puddles: Puddle[];
        pagination: any;
    }

    const [puddles, setPuddles] = useState<Data>();
    let page = searchParams.get('page');

    useEffect(() => {
        Fetch(`/api/v1/humor-pump?page=${page}`, setPuddles);
    }, [page]);

    useEffect(() => {
        Fetch("/api/v1/humor-pump", setPuddles);
    }, []);

    return(
        <main className="humor-pump-page">
            <ul>
                {puddles.map(p => (<li>{p.body}</li>))}
            </ul>
        </main>
    )

}

export default HumorPump;
